/*custom general*/
jQuery (document).ready (function () {
   /*find */
  jQuery ('body').on ('keyup','#search-on-table', function (e) {
      var string = jQuery (this).val ().toLowerCase();
      var highlightText = jQuery (this).data ('highlight-text');
      var itemsClass = jQuery (this).data ('search-items');
      var items = jQuery (itemsClass);
      if ( items.length == 0 )
        return;
      if (string.length == 0) {
            jQuery (itemsClass).show(); 
            jQuery (itemsClass).find (highlightText).removeHighlight();
            return;
      }
      jQuery (itemsClass).find (highlightText).removeHighlight();

      jQuery (itemsClass).each (function (index, elem) {
            var text = jQuery (this).find ('td').text ();
            if (text.toLowerCase().indexOf (string) != -1 ) {
                jQuery (elem).show ();
                jQuery (elem).find (highlightText).highlight (string,{ caseSensitive: true }); 
            }  else  { 
                jQuery (elem).hide (); 
            } 
      });
     
  });         
})

/*creates a remote select2*/
function remote_select2 ( _elems ) {

    jQuery.each ( jQuery (_elems), function (index, element ) {
        var api = jQuery (element).data ('api');
        var placeholder = jQuery (element).data ('placeholder');
        jQuery ( element ).select2({
            placeholder: placeholder,
            language: {
                noResults: function (params) {
                  return "No se encontraron resultados. Presiona la tecla Enter para adicionarlo.";
                }
            } ,                
            ajax: {                   
              url: "/" + api,
              dataType: 'json',
              delay: 250,
              processResults: function (data) {
                return {
                  results: data
                };
              },
              cache: true
            }
        }); 
    });
}
/*adds a new item to select2*/
function addItemToSelect ( select, value ) {
    var newOption = new Option ( value, value, false, false);    
    jQuery (select).append(newOption).trigger('change');
    jQuery (select).val (value);    
    jQuery (select).select2 ("close");   
} 